[{"fieldname": "print_heading_template", "fieldtype": "Custom HTML", "options": "<div class="print-heading text-center">				<h2><div>Wellness Center</div>	</h2>
    <p><strong>Service Booking Confirmation</strong></p>
</div>"}, {"fieldtype": "Section Break", "label": ""}, {"fieldtype": "Column Break"}, {"fieldname": "_custom_html", "print_hide": 0, "label": "Custom HTML", "fieldtype": "HTML", "options": "<style>
  @media print {
    .footer {
      position: fixed;
      bottom: 30px;
      left: 0;
      right: 0;
    }
  }
  .booking-container {
    width: 700px;
    margin: auto;
    font-family: "Helvetica Neue", Helvetica, Arial, sans-serif;
    font-size: 14px;
    color: #333;
  }

  .header {
    text-align: center;
    margin-bottom: 30px;
  }

  .header h2 {
    margin: 0;
    color: #2c3e50;
  }

  .header p {
    margin: 4px 0 0 0;
    font-size: 13px;
    color: #555;
  }

  .details-table {
    width: 100%;
    border-collapse: collapse;
    margin-bottom: 30px;
  }

  .details-table th,
  .details-table td {
    padding: 10px;
    text-align: left;
    vertical-align: top;
  }

  .details-table th {
    width: 35%;
    background-color: #f9f9f9;
    color: #000;
  }

  .details-table td {
    background-color: #fcfcfc;
  }

  .footer {
    text-align: center;
    font-size: 12px;
    color: #777;
  }

  .footer p {
    margin: 4px 0;
  }
</style>

<div class="booking-container">

  <table class="details-table">
    <tr>
      <th>Booking Reference</th>
      <td>{{ doc.name }}</td>
    </tr>
    <tr>
      <th>Customer Name</th>
      <td>{{ doc.customer_name }}</td>
    </tr>
    <tr>
      <th>Service Type</th>
      <td>{{ doc.service_type }}</td>
    </tr>
    <tr>
      <th>Preferred Date & Time</th>
      <td>{{ frappe.format_value(doc.preferred_datetime, doc.meta.get_field("preferred_datetime")) }}</td>
    </tr>
    <tr>
      <th>Status</th>
      <td>{{ doc.status }}</td>
    </tr>
    <tr>
      <th>Date Issued</th>
      <td>{{ frappe.utils.formatdate(doc.creation) }}</td>
    </tr>
  </table>

  <div class="footer">
    <p>Thank you for choosing our services!</p>
    <p>Contact us: <a href="mailto:wellnesscenter@example.com">wellnesscenter@example.com</a> | +880-1234-567890</p>
  </div>
</div>
"}]
